/** SpaceGen logo as inline SVG (no external asset in iframe). */
export function SpaceGenLogo({ size = 26 }: { size?: number }) {
  const h = (19 / 26) * size;
  return (
    <svg
      width={size}
      height={h}
      viewBox="0 0 26 19"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      aria-label="SpaceGen"
    >
      <path d={SPACEGEN_PATH} fill="black" />
    </svg>
  );
}

const iconSize = 24;

/** Platform icons from bundled SVG URLs (?url). Fallback for platforms without asset. */
export function PlatformIcon({
  platformId,
  size = iconSize,
  color = "currentColor",
}: {
  platformId: string;
  size?: number;
  color?: string;
}) {
  const url = PLATFORM_LOGO_URLS[platformId];
  if (url) {
    return (
      <img
        src={url}
        alt=""
        width={size}
        height={size}
        style={{ display: "block", objectFit: "contain" }}
      />
    );
  }
  const s = size;
  const vb = 24;
  return (
    <svg
      width={s}
      height={s}
      viewBox={`0 0 ${vb} ${vb}`}
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      aria-hidden
    >
      <circle cx="12" cy="12" r="8" fill={color} opacity={0.3} />
      <text
        x="12"
        y="16"
        textAnchor="middle"
        fill={color}
        fontSize="10"
        fontWeight="600"
        fontFamily="Inter, sans-serif"
      >
        {platformId.slice(0, 1).toUpperCase()}
      </text>
    </svg>
  );
}
